        <script>
        function findTotal(){
        var arr = document.getElementsByName('usuarios','recursos','procesos', 'urgencia');
        var tot=0;
        for(var i=0;i<arr.length;i++){
        if(parseInt(arr[i].value))
        tot += parseInt(arr[i].value);
    }
        document.getElementById('total').value = tot;
        }
        </script>
